module.exports = {

  ajax: function(url, success, error, data) {
    var xhr = new XMLHttpRequest();
    xhr.open("GET", url, true);
    xhr.responseType = "json";
    xhr.setRequestHeader("Content-Type", "application/json");
    xhr.onload = function(event) {
      success(xhr.response);
    }
    xhr.onerror = error;
    xhr.send(data);
  },
  getUrlParameter: function(parameterName) {
    var name = (new RegExp('[?&]' + encodeURIComponent(parameterName) + '=([^&]*)')).exec(window.location.search);
    if (name)
      return decodeURIComponent(name[1]);
  },
  saveGrid: function(host, data) {
    var storedData = getStoredData();
    storedData[host] = data;
    window.localStorage.setItem('signalkGrid3', JSON.stringify(storedData));
  },
  retrieveGrid: function(host) {
    return getStoredData()[host];
  }
}

function getStoredData() {
  var storedData = {};
  try {
    var fromLocal = window.localStorage['signalkGrid3'];
    if (fromLocal) {
      storedData = JSON.parse(fromLocal);
    }
  } 
  catch (ex) {
    console.error(ex);
  }
  return storedData;
}
var WebSocket = require('ws');
var debug = require('debug')('signalk:ws');
var browser = require('./ui/util/browser');
var Promise = require('bluebird');


function connectDelta(hostname, callback, onConnect, onDisconnect) {
  debug("Connecting to " + hostname);
  var url = "ws://" + hostname + "/signalk/stream?stream=delta&context=self";
  if (typeof Primus != 'undefined') {
    debug("Using Primus");
    var signalKStream = Primus.connect(url, {
      reconnect: {
        maxDelay: 15000,
        minDelay: 500,
        retries: Infinity
      }
    });
    signalKStream.on('data', callback);
    return {
      disconnect: function() {
        signalKStream.destroy();
        debug('Disconnected');
      }
    }
  } else {
    debug("Using ws");
    var connection = new WebSocket(url);
    connection.onopen = function(msg) {
      debug("open");
      var sub = '{"context":"vessels.self","subscribe":[{"path":"*"}]}';
      connection.send(sub);
      onConnect();
    };
    connection.onerror = function(error) {
      debug("error:" + error);
    };
    connection.onmessage = function(msg) {
      callback(JSON.parse(msg.data));
    };
    return {
      disconnect: function() {
        connection.close();
        debug('Disconnected');
      }
    }
  }
}

function getSelf(host) {
  return new Promise(function(resolve, reject) {
    browser.ajax("http://" + host + "/api/v1/vessels/self", resolve, reject);
  });
}

function getSelfMatcher(host) {
  return getSelf(host).then(function(selfData) {
    var selfId = selfData.mmsi || selfData.uuid;
    var selfContext = 'vessels.' + selfId;
    return function(delta) {
      return result = delta.context === 'self' || delta.context === 'vessels.self' || delta.context === selfContext;
    }
  });
}

module.exports = {
  connectDelta: connectDelta,
  getSelf: getSelf,
  getSelfMatcher: getSelfMatcher
}
